import React from 'react'
import style from './index.module.css'

const Dialog = props => {
  const { userId, handleInput, connectToChatkit } = props;

  return (
    <section className="chat-screen">
        <form onSubmit={connectToChatkit}>
          <label className="username-label" htmlFor="username">
            Login with your username
          </label>
          <input
            id="username"
            className="username-input"
            autoFocus
            type="text"
            name="userId"
            value={userId}
            onChange={handleInput}
            placeholder="Enter your username"
          />
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </form>
    </section>
  );
};

Dialog.propTypes = {
  userId: Proptypes.string.isRequired,
  handleInput: Proptypes.func.isRequired,
  connectToChatkit: Proptypes.func.isRequired,
};

export default CreateRoomForm;
export const CreateRoomForm = ({ submit }) => (
  <form
    className={style.component}
    onSubmit={e => {
      e.preventDefault()
      submit({
        name: e.target[0].value,
        private: e.target.elements[2].checked,
      })
      e.target[0].value = ''
    }}
  >
    <input placeholder="Create a Room" />
    <button>
      <input type="checkbox" />
      <svg>
        <use xlinkHref="index.svg#lock" />
      </svg>
    </button>
    <button type="submit">
      <svg>
        <use xlinkHref="index.svg#add" />
      </svg>
    </button>
  </form>
)
