import React from 'react'
import Multiselect from 'multiselect-dropdown-react';

const CreatePrivateRoomForm = props => {
  const { sendGroupMessage, users, handleInput, dmUser } = props;
  const options = users.map(function(user){
    return {name: user["id"], value: user['id']}
  })
  console.log(options)

  return (
    <section className="chat-screen">
      <label className="username-label" htmlFor="username">
        Group Channel Name
      </label>
      <input
        id="name"
        autoFocus
        type="text"
        name="dmUser"
        value={dmUser}
        onChange={handleInput}
        placeholder="Enter your username"
      />
      <div>
        <Multiselect options={options} name="userIds" value={userIds} onSelectOptions={handleInput} />
      </div>
      <button type="submit" className="submit-btn" onClick={() => sendGroupMessage()}>
        Submit
      </button>
    </section>
  );
};

export default CreatePrivateRoomForm;
