import React from 'react'
import MultiSelect from "@khanacademy/react-multi-select";
import MultiSearchSelect from "react-search-multi-select";

const CreateRoomForm = props => {
  const { sendGroupMessage, users, handleInput, channelName, userIds } = props;
  const options = users.map(function(user){
    return {label: user["id"], value: user['id']}
  })

  function check(e){
    console.log("success")
    handleInput(e)
  }
  return (
    <section className="chat-screen">
        <form onSubmit={sendGroupMessage}>
          <label className="username-label" htmlFor="username">
            Group Channel Name
          </label>
          <input
            id="name"
            autoFocus
            type="text"
            name="channelName"
            value={channelName}
            onChange={handleInput}
            placeholder="Enter your username"
          />
          <div>
            <MultiSelect
              options={options}
              name="userIds"
              selected={userIds}
              onSelectedChanged={selected => handleInput}
            />
          </div>
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </form>
    </section>
  );
};

export default CreateRoomForm;
